from operator import abs
def readArray():
    s = input().split(" ")
    l = []
    for i in s:
        if len(i) != 0:
            l.append(int(i))
    return l

def smallestdifference(a):
    if(a==[]):
        return(-1)
    d=10**20
    for i in range(len(a)-1):
        for j in range(i+1,len(a)):
            if(abs(a[i]-a[j])<d):
                d=abs(a[i]-a[j])
    return (d)
